func (trs *TcpRuleSelect) sqlScan(ctx context.Context, v interface{}) error {
	for _, f := range trs.fields {
		if !tcprule.ValidColumn(f) {
			return &ValidationError{Name: f, err: fmt.Errorf("invalid field %q for selection", f)}
		}
	}
	rows := &sql.Rows{}
	query, args := trs.sqlQuery().Query()
	if err := trs.driver.Query(ctx, query, args, rows); err != nil {
		return err
	}
	defer rows.Close()
	return sql.ScanSlice(rows, v)
}

func (trs *TcpRuleSelect) sqlQuery() sql.Querier {
	selector := trs.sql
	selector.Select(selector.Columns(trs.fields...)...)
	return selector
}
